PATH_TO_JAVY="bin"
if [[ ! -f "$PATH_TO_JAVY/javy" ]]; then
    TMPGZ=$(mktemp)
    #VERSION_MAJOR="19"
    #VERSION_MINOR="0"
    if [[ "$(uname -s)" == "Darwin" ]]; then
        curl -OL https://github.com/Jacarte/javy/releases/download/node-red0.1/javy-x86_64-macos-node-red0.1.gz
        NAME=javy-x86_64-macos-node-red0.1.gz
        mkdir -p $PATH_TO_JAVY
        gunzip $NAME 
    else
        curl -OL https://github.com/Jacarte/javy/releases/download/node-red0.1/javy-x86_64-linux-node-red0.1.gz
        NAME=javy-x86_64-linux-node-red0.1.gz
        mkdir -p $PATH_TO_JAVY
        tar -xf  $NAME  -C $PATH_TO_JAVY --strip-components=1
        mv $PATH_TO_JAVY/javy-* $PATH_TO_JAVY/javy
    fi
fi

