PATH_TO_JAVY="javy"
if [[ ! -d $PATH_TO_JAVY ]]; then
    TMPGZ=$(mktemp)
    #VERSION_MAJOR="19"
    #VERSION_MINOR="0"
    if [[ "$(uname -s)" == "Darwin" ]]; then
        curl --fail --location --silent https://github.com/Jacarte/javy/suites/13053631943/artifacts/707792737 --output $TMPGZ
    else
        curl --fail --location --silent https://github.com/Jacarte/javy/suites/13053631943/artifacts/707792734 --output $TMPGZ
    fi
    mkdir $PATH_TO_JAVY
    tar xf $TMPGZ -C $PATH_TO_JAVY --strip-components=1
fi

