PATH_TO_JAVY="bin"
if [[ ! -f "$PATH_TO_JAVY/javy" ]]; then
    TMPGZ=$(mktemp)
    #VERSION_MAJOR="19"
    #VERSION_MINOR="0"
    if [[ "$(uname -s)" == "Darwin" ]]; then
        wget -O javy.gz https://github.com/Jacarte/javy/releases/download/node-red0.1/javy-x86_64-macos-node-red0.1.gz
    else
        wget -O javy.gz https://github.com/Jacarte/javy/releases/download/node-red0.1/javy-x86_64-linux-node-red0.1.gz
    fi
    mkdir $PATH_TO_JAVY
    tar xf javy.gz -C $PATH_TO_JAVY --strip-components=1
fi

